import React from 'react';
import Link from 'next/link';
import PropTypes from 'prop-types';
import {Col, Input, Menu, Row, Tabs} from 'antd';
const { TabPane } = Tabs;
import {useSelector} from 'react-redux';
import styled, {createGlobalStyle} from 'styled-components';

import MainPageHeader from "./Header/MainPageHeader";
// import MainPageContents from "./MainPage/MainPageContents";

const Global = createGlobalStyle`
  body {
    background-color: #fff;
  }

  .ant-row {
    margin-right: 0 !important;
    margin-left: 0 !important;
  }

  .ant-col:first-child {
    padding-left: 0 !important;
  }

  .ant-col:last-child {
    padding-right: 0 !important;
  }
`;
const ContentsWrapper = styled.div`
  position: relative;
  max-width: 1800px;
  padding-right: 60px;
  padding-left: 60px;
  margin: 0 auto;
  z-index: -1;
`;

const AppLayout = ({children}) => {
    const {isLoggedIn} = useSelector(state => state.user);
    return (
        <>
            {/* <Global/> */}
            <MainPageHeader/>
            <ContentsWrapper>
                {children}
                <Tabs defaultActiveKey="1">
              <TabPane tab="타워 설계 설정" key="1">
                Content of Tab Pane 1
              </TabPane>
              <TabPane tab="세그먼트 설정" key="2">
                Content of Tab Pane 2
              </TabPane>
              <TabPane tab="기타" key="3">
                Content of Tab Pane 3
              </TabPane>
            </Tabs>
            </ContentsWrapper>
        </>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;
